import React, { useRef, useState } from "react";
import "./Video.css";
import VideoFooter from "./VideoFooter";

const Video = () => {
  const [playing, setPlaying] = useState(false);
  const videoRef = useRef(null);

  const handleVideoPress = () => {
    if (playing) {
      videoRef.current.pause();
      setPlaying(false);
    } else {
      videoRef.current.play();
      setPlaying(true);
    }
  };

  return (
    <div className="video">
      <video
        onClick={handleVideoPress}
        className="video__player"
        loop
        ref={videoRef}
        src="https://v77.tiktokcdn.com/758fcc7547091fa443fde2d68876d075/5f53e4e4/video/tos/useast2a/tos-useast2a-pve-0068/c657fb2c5ff24732a242da9e00746bda/?a=1233&br=1742&bt=871&cr=0&cs=0&cv=1&dr=0&ds=3&er=&l=202009051319550101890740124A1CE99A&lr=tiktok_m&mime_type=video_mp4&qs=0&rc=ajZnNmYzO3htdjMzNzczM0ApNjRoOztmZmQzNzc3OWU5NWdrL3ByMmdpM2hfLS0yMTZzczA2YC8yMTIwNDYvLmNeXzI6Yw%3D%3D&vl=&vr="
      ></video>

      <VideoFooter />
    </div>
  );
};

export default Video;
